import React from 'react';

class Slide extends React.Component {


  render() {
    return (
      <div className="slide" id={this.props.idName}>
        <div className="slide-content">
          <h3>{this.props.data.title}</h3>
          <p>{this.props.data.body}</p>
          <p>Slide {this.props.index}</p>
          <p><span onClick={this.props.prevClick}>Prev</span> / <span onClick={this.props.nextClick}>Next</span></p>
        </div>
        <div className="slide-image">
          <img src={this.props.data.thumbUrl} alt={this.props.data.thumbAlt} />
          <div className="slide-image-white"> </div>
        </div>
      </div>
    )
  }
}

export default Slide;
