import React from 'react';

const Slide = (props) => {


  let whiteBgClass = '';

  if (props.index % 2 == 0) {
    whiteBgClass = 'slide-content-rt'
  } else {
    whiteBgClass = 'slide-content-lt'
  }

    return (
      <div className="slide" id={props.idName}>
        <div className={whiteBgClass}>
          <h3>{props.data.title}</h3>
          <p>{props.data.body}</p>
          <p>Slide {props.index + 1} of {props.totalSlides}</p>
          <p><span onClick={props.prevClick}>Prev</span> / <span onClick={props.nextClick}>Next</span></p>
        </div>
        <div className="slide-image">
          <img src={props.data.thumbUrl} alt={props.data.thumbAlt} />
        </div>
        <div className="slide-image-white"> </div>
      </div>
    )
  }


export default Slide;
